import React, { useState } from 'react';
import onSwitch from '../img/switch-on-outline.svg'
import offSwitch from '../img/switch-off-outline.svg'


function Task({id, name, completed }) {

    const [complete, setComplete] = useState(completed)

  return (
    <div>
        <h2>{name}</h2>
          {completed === true ? <img src={onSwitch} onClick={() => setComplete(!complete)} /> : <img src={offSwitch} onClick={() => setComplete(!complete)} />}
    </div>
  )
}

export default Task
