import React, { useContext } from 'react'
import { ProductContext, productDataContext } from '../../context/productContext';

const Product = () => {

    const { data, setData } = useContext( productDataContext )

  return (
    <div className='card text-bg-secondary mb-3 ms-4'>
        <div className='card-header'>{data.name}</div>
        <div className='card-body'>
            <h2 className='card-text'>{data.amount}</h2>
            <h3 className='card-text'>{data.price}</h3>
            <h4 className='card-text'>{data.IVA}</h4>
        </div>
    </div>
  );
};

export default Product
