import React, { useRef, useState } from 'react'
import Task from './Task';


function TasksList() {

    const taskObjects = [
        {
            id: 0, 
            name: 'Hello Im a task'
        }
    ]

    const [tasks, setTasks] = useState(taskObjects);

    const nameRef = useRef();

    const addTask = (e) => {
        e.preventDefault()

        const newTask = {
            name: nameRef.current.value, 
            completed: false 
        }

        const temporalTask = [...tasks];
        temporalTask.push(newTask);
        setTasks(temporalTask);
    }


    return (
        <div style={{ display: 'flex', flexDirection: 'column' }}>
            {
                tasks.map((task, key) =>
                    <Task name={task.name} completed={task.completed} key={task.id}></Task>
                )
            }
            <input type='text' name='name' placeholder='name' ref={nameRef}></input>
            <button onClick={addTask}>Add</button>
        </div>
    )
}

export default TasksList
