        <div className='card-body'>
            <h2 className='card-text'>Amount {product.amount}</h2>
            <h3 className='card-text'>Price {product.price}</h3>
            <h4 className='card-text'>IVA {product.IVA}</h4>
        </div>
    </div >

        


      } )
      
   }

};

