import React, { useState } from 'react';
import onSwitch from '../img/switch-on-outline.svg'
import offSwitch from '../img/switch-off-outline.svg'


function Task({id, name, completed }) {

    const [complete, setComplete] = useState(completed)

  return (
    <div>
        <h2>{name}</h2>
          {complete === true ? 
          (<div><h3>Completed</h3>
          <img src={onSwitch} onClick={() => setComplete(!complete)} /></div>) 
          : 
              (<div><h3>Not completed</h3><img src={offSwitch} onClick={() => setComplete(!complete)} /></div>)}
    </div>
  )
}

export default Task
