import React, { useContext, useRef, useState } from 'react'
import { productDataContext } from '../../context/productContext';

const FormList = () => {

  const { data, setData } = useContext( productDataContext );

  const nameRef = useRef('');
  const amountRef = useRef();
  const priceRef = useRef();
  const ivaRef = useRef();

  const addProduct = e => {
    e.preventDefault();
    const newProduct = {
      name: nameRef.current.value,
      amount: amountRef.current.value,
      price: priceRef.current.value,
      IVA: ivaRef.current.value,
    };
    data.push(newProduct)
  };

  return (
    <form>
        <label htmlFor='name'>Name: </label>
        <input ref={nameRef} type='text' name='name'></input>
        <label htmlFor='amount'>Amount</label>
        <input ref={amountRef} type='number' name='amount'></input>
        <label htmlFor='price'>Price </label>
        <input ref={priceRef}  type='number' name='price'></input>
        <label></label>
        <input ref={ivaRef}  type='number'></input>
        <button onSubmit={addProduct} type='submit'>Submit</button>
    </form>
  )
}

export default FormList
